import React from "react";
import AdminNav from './Nav';
import ngo from '../img/ngo.jpg';
import './addngo.css';

const admin = {
    backgroundImage: 'url(' + ngo + ')',
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%',
}

class Addngo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email : '',
      name : '',
      address : '',
      phone:''
    }
    this.handleNameChange=this.handleNameChange.bind(this)
    this.handleEmailChange=this.handleEmailChange.bind(this)
    this.handleAddressChange=this.handleAddressChange.bind(this)
    this.handlePhoneChange=this.handlePhoneChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
}



handleNameChange=event=>{
  this.setState({
    name : event.target.value
  });
}

handleEmailChange=event=>{
  this.setState({
    email: event.target.value
  });
}

handlePhoneChange=event=>{
  this.setState({
    phone: event.target.value
  });
}

handleAddressChange=event=>{
  this.setState({
    address: event.target.value
  });
}

handleSubmit = event => {

  event.preventDefault();
  console.log(this.state)
   var body = {

    email : this.state.email,
    ngo : this.state.name,
    address : this.state.address,
    phone:this.state.phone

  }
  console.log(body);
  if(this.state.name==""){
    alert('Please enter the name')
  }

  else if(this.state.email==""){
    alert('Please enter the email')
  }
  else if(this.state.phone==""){
    alert('Please enter the phone')
  }
  else if(this.state.address==""){
    alert('Please enter the address')
  }
  else{

const url = "https://localhost:9000/addngo";
  let headers = new Headers();

  headers.append('Content-Type','application/json');
  headers.append('Accept','application/json');

  headers.append('Access-Control-Allow-origin',url);
  headers.append('Access-Control-Allow-Credentials','true');

  headers.append('POST','GET');

  fetch(url, {
     headers:headers,
     method: 'POST',
     body: JSON.stringify(body)
  }).then(response => {if(response){
    alert('NGO Details submitted');
  }
  })

}
}

  render() {
    return (

      <div style={admin} class="adminmain">
          <AdminNav/>
          <form>
              <div class="adminc">
                <h1>ADDING NGO FORM..</h1>

                <label for="ngo"><b>NGO name :</b></label>
                <input type="text" placeholder="ngo name" name="ngo" value={this.state.name} onChange={this.handleNameChange} required/>

                <label for="email"><b>Email :</b></label>
                <input type="text" placeholder="Enter Email" name="email" value={ this.state.email } onChange = { this.handleEmailChange } required/>

                <label for="phno"><b>Phone number :</b></label>
                <input type="text" placeholder="phone number.." name="phno" value={ this.state.phone } onChange = { this.handlePhoneChange } required/>

                <label for="addr"><b>Address :</b></label>
                <textarea placeholder="Enter NGO address" name="addr" value={ this.state.address} onChange = { this.handleAddressChange } required></textarea>

                <center>
                <button type="submit" class="addbtn" name="add" onClick={this.handleSubmit.bind(this)}>Add NGO</button></center>
              </div>

            </form>
        </div>

);
}
}
export default Addngo
