import React from "react";
import pit from '../img/pit.jpg';
import '../Home.css';
export class Register extends React.Component {

  handleSubmit(event){
    event.preventDefault()
  }
  handleChange(event){
    const { name, value } = event.target
    this.setState({
      [name] : value
    })
  }

  render() {
    return (
      <div>
      <form onSubmit = {this.handleSubmit }>
      <div className="base-container">
        <div className="header">Register</div>
        <div className="content">
          <div className="form">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" name="name" placeholder="name" onChange = { this.handleChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="text" name="email" placeholder="email" value={ this.state.email } onChange = { this.handleChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="text" name="password" placeholder="password" value={ this.state.password} onChange = { this.handleChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input type="text" name="address" placeholder="address" onChange = { this.handleChange } required/>
            </div>
          </div>
        </div>
        <div className="footer">
          <button type="submit" className="btn">
            Register
          </button>
          <br/><br/><br/>
          <a href="/home"><u>back</u></a>
        </div>
      </div>
      </form>
      </div>

    );
  }
}
