import React from "react";
import donorhome from '../img/donorhome.jpg';
import './d_home.css';
import './Nav.css';
import pit from '../img/pit.jpg';

var eid,profile,image1,image2,message,ngo,s;


const donor ={
    backgroundImage: 'url(' + donorhome+ ')',
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%',
}

class Donor extends React.Component {

    constructor(props) {
      super(props);
      this.state= {
          ngo:[],

      }


    this.difproChange = this.difproChange.bind(this);
    this.dateChange = this.dateChange.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
    this.sngoChange=this.sngoChange.bind(this)


    eid=sessionStorage.getItem("id")
    profile="/donorprofile";
    console.log(eid)
  }

  componentDidMount(){
     console.log("In cdm")
      const url = "http://localhost:9000/ngos";

      let headers = new Headers();
      headers.append('Content-Type','application/json');
      headers.append('Accept','application/json');

      headers.append('Access-Control-Allow-origin',url);
      headers.append('Access-Control-Allow-Credentials','true');

      headers.append('POST','GET');

  fetch(url,{
          headers:headers,
          method: 'GET',
      })
      .then(response=>
      {                
          return response.json()
      }).then((res)=>{
                   this.setState({ngo:res})
                 //  console.log(typeof(this.sate.ngo))
                  //  console.log(this.sate.ngo),
                  //  console.log(this.sate.ngo[0]),
                  //  console.log(this.sate.ngo[1])
                })          
  }
  dd()
  {
    let s=this.state.ngo;

    return s.map((song) => {
       return (
       <option value={song}>{song}</option>
       );
   });  
  }  





  sngoChange=event=>{
    sessionStorage.setItem("sngo",event.target.value)
  }



  difproChange=event=>{
    sessionStorage.setItem("difpro",event.target.value)
  }


  dateChange=event=>{
    sessionStorage.setItem("date",event.target.value)
  }


  handleSubmit(event) {

    event.preventDefault();
    console.log(this.state)
     var body = {
        did: eid,
        pname: this.state.pname,
        quant: this.state.quant,
        quality: this.state.quality,
        expdate: this.state.date,
        des: this.state.des,
        cat: this.state.category,
        scat:this.state.scat,
        type:this.state.type,
        img1: image1,
        img2: image2,
        state: 0,
        ngo:this.state.sngo
    }
    console.log(body);
    const url = "http://localhost:9000/addproduct";
    let headers = new Headers();

    headers.append('Content-Type','application/json');
    headers.append('Accept','application/json');

    headers.append('Access-Control-Allow-origin',url);
    headers.append('Access-Control-Allow-Credentials','true');

    headers.append('POST','GET');

    fetch(url, {
       headers:headers,
       method: 'POST',
       body: JSON.stringify(body)
    })
    .then(response => {if(response.ok){
          alert("Details submitted.. Thank You For Donating..");
        window.location.reload(false);
