import React from "react";
import AdminNav from './Nav';




const admin = {
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%', 
}

const table = {
    top:'20%',
    left:'40%'
}

class CartProfile extends React.Component {

    constructor(props) {
        super(props);
         this.state={
           s:[],
           p:null,
           sf:false
         }


       }



    renderResultRows(){

        let s=this.state.s
        let n
        return s.map((song,index) => {
           let i='/productimages/'+song[4]
           //console.log(i,typeof(i))
           let a="nprice-"+index
            return (
                <tr id={index}>

                    <td >{song[1]}</td>
                    <td >{song[3]}</td>
                    <td><img src={i} class="validateimage"/></td>
                    <td contentEditable="true" id={a} width="100px"></td>
                    <td><button onClick={() => this.fetchDetails(index)} > Validate </button></td>
                    <td><button onClick={() => this.deleteDetails(index)} > Delete </button></td>
                     </tr>
            );
        });  


    }

    deleteDetails(row){
        let s=this.state.s
        var body={"pid":s[row][0]}
        const url = "http://localhost:9000/deleteproduct";

        let headers = new Headers();
        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

         fetch(url,{
            headers:headers,
            method: 'POST',
            body: JSON.stringify(body)
            })
            .then(response => {if(response.ok){
                alert("Details deleted");
              window.location.reload(false);
            }
          })

    }

    fetchDetails(row){
        let s=this.state.s
        let a="nprice-"+row
        let n=document.getElementById(a).innerHTML;
        //  console.log(row,n)
        var body={"pid":s[row][0],"np":n}
        console.log()
        const url = "http://localhost:9000/updateproduct";

        let headers = new Headers();
        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

         fetch(url,{
            headers:headers,
            method: 'POST',
            body: JSON.stringify(body)
            })
            .then(response => {if(response.ok){
                alert("Details submitted.. Thank You For Donating..");
              window.location.reload(false);
            }
          })

    }

   products(){
        const url = "http://localhost:9000/products";

        let headers = new Headers();
        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

         fetch(url,{
            headers:headers,
            method: 'GET',
            })
            .then(response=>{                
                return response.json()
            }).then(res=>{this.setState({s:res})
           // console.log(this.state.s) 
        })



        }
        componentDidMount(){this.products()}

        render() {
            let i='/productimages/bajaj1.jpeg'

            return (

                <div style={admin} class="adminmain">
                    <AdminNav/>

                    <table id="product" class="atab" border="1">
                        <th>Name</th>
                        <th>Category</th>
                        <th>Image</th>
                        <th>Nominal Price</th>
                        <th>Validation</th>
                        <th>Remove</th>
                     <tbody> {this.renderResultRows()} </tbody>
                    </table>

                    {/* <img src={window.location.origin + "/productimages/drafter.jpg"}/> */}
                </div>
            )
        }
}

export default CartProfile
