const admin = {
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%', 
}

const table = {
    top:'20%',
    left:'40%'
}

class Validate extends React.Component {

    constructor(props) {
        super(props);
         this.state={
           s:[],
           p:null,
           sf:false
         }
    //this.updatenominal=this.updatenominal.bind(this)
    this.onCellChange=this.onCellChange.bind(this)

       }

   onCellChange(event){

    console.log("in cell change:"+event.target.value)
    } 

    renderResultRows(){

        let s=this.state.s
        let n
        return s.map((song,index) => {
           let i='/productimages/'+song.img1
           console.log(i,typeof(i))
            return (
                <tr >

                    <td >{song.pname}</td>
                    <td >{song.did}</td>
                    <td >{song.quant}</td>
                    <td >{song.quality}</td>
                    <td><img src={window.location.origin + i}/></td>
                    <td id="nprice" contentEditable="true"></td>



                    <td><button onClick={() => {this.state.sf=true
                        this.fetchDetails(index)}} /></td>
                </tr>
            );
        });  


    }

    fetchDetails(row){
    //     let s=this.state.s
    //     //console.log(s[row])
    //     sessionStorage.setItem("pname",s[row].pname)
    //    // console.log(sessionStorage.getItem("pname"))
    //     sessionStorage.setItem("pname",s[row].img1)
    //     sessionStorage.setItem("pname",s[row].cat)
    //     sessionStorage.setItem("pname",s[row].quant)
    //     sessionStorage.setItem("pname",s[row].pid)
    //    window.location.href="popupform/:"+s[row]
    return (
        <div> 
        <form id= "add-app">

            <label>Application Name : </label>
            <input type="text"> </input>

            <label> id : </label>
            <input type="text" ></input>

            <label>Server details : </label>
            <input ></input>

            <button>Create</button>
         </form>
         </div>
        );
    }


   products(){
        const url = "http://localhost:9000/products";

        let headers = new Headers();
        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

         fetch(url,{
            headers:headers,
            method: 'GET',
            })
            .then(response=>{                
                return response.json()
            }).then(res=>{this.setState({s:res})
            console.log(this.state.s) })



        }
        componentDidMount(){this.products()}

        render() {
            let i='/productimages/bajaj1.jpeg'

            return (

                <div style={admin} class="adminmain">
                    <AdminNav/>

                    <table id="product" class="atable" border="1" onCellChange={this.onCellChange}>
                      {this.renderResultRows()} 
                    </table>

                    <img src={window.location.origin + i}/>
                </div>
            )
        }
}

export default Validate
