import React, { Component } from 'react';
import FormUserDetails from './FormUserDetails';
import FormPersonalDetails from './FormPersonalDetails';
import Confirm from './Confirm';
import Success from './Success';

export class UserForm extends Component {
  state = {
    step: 1,
        pname:null,
        quant:null,
        quality:null,
        date:null,
        des:null,
        category:null,
        file1:null,
        file2:null,
        ngo:[],
        difproducts:null,
        sngo:null,
        scat:null, 
  };

  // Proceed to next step
  nextStep = () => { 
    const { step } = this.state;
    this.setState({
      step: step + 1
    });
  };



  // Handle fields change
  handleChange = input => e => {
    this.setState({ [input]: e.target.value });
  };

  render() {
    const { step } = this.state;
    const { pname, quant, quality, date, des, category, file1, file2, ngo, type, sngo,scat } = this.state;
    const values = { pname, quant, quality, date, des, category, file1, file2, ngo, type, sngo,scat };

    switch (step) {
      case 1:
        return (
          <FormUserDetails
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      default:
        return (
          <FormPersonalDetails
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
    }
  }
}

export default UserForm;
