import React from "react";
import AdminNav from './Nav';
import ngo from '../img/ngo.jpg';
import './addngo.css';

const admin ={
    backgroundImage: 'url(' + ngo + ')',
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%',
}

class Addngo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
    }
    this.handleImage = this.handleImage.bind(this)
}

handleImage(event) {
  this.setState({
    file: URL.createObjectURL(event.target.files[0]),
  })
}
  render() {
    return (

      <div style={admin} class="adminmain">
          <AdminNav/>
          <form>
              <div class="adminc">
                <h1>ADDING NGO FORM..</h1>

                <label for="ngo"><b>NGO name :</b></label>
                <input type="text" placeholder="ngo name" name="ngo" required/>

                <label for="email"><b>Email :</b></label>
                <input type="text" placeholder="Enter Email" name="email" required/>

                <label for="phno"><b>Phone number :</b></label>
                <input type="text" placeholder="phone number.." name="phno" required/>

                <label for="addr"><b>Address :</b></label>
                <textarea placeholder="Enter NGO address" name="addr" required></textarea>


                <center>
                <input type="submit" class="addbtn" name="add" value="Add NGO"/></center>
              </div>

            </form>
        </div>

);
  }
}
export default Addngo
