import React from "react";

import '../Home.css';
export class Register extends React.Component {

  constructor(props) {
    super(props);

    this.state={

      email : '',
      password : '',
      name : '',
      address : '',
      phone:''

    }
    this.handleNameChange=this.handleNameChange.bind(this)
    this.handlePasswordChange=this.handlePasswordChange.bind(this)
    this.handleEmailChange=this.handleEmailChange.bind(this)
    this.handleAddressChange=this.handleAddressChange.bind(this)
    this.handlePhoneChange=this.handlePhoneChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)

  }

  handleSubmit(event){
    event.preventDefault()
  }
  handleNameChange=event=>{
    this.setState({
      name : event.target.value
    });
  }

  handlePasswordChange=event=>{
    this.setState({
      password : event.target.value
    });
  }

  handleEmailChange=event=>{
    this.setState({
      email: event.target.value
    });
  }

  handlePhoneChange=event=>{
    this.setState({
      phone: event.target.value
    });
  }

  handleAddressChange=event=>{
    this.setState({
      address: event.target.value
    });
  }


  handleSubmit(event) {

    event.preventDefault();
     var body = {

      email : this.state.email,
      password : this.state.password,
      name : this.state.name,
      address : this.state.address,
      phone:this.state.name

    }
    console.log(body);
    if(this.state.name==""){
      alert('Please enter the name')

    }
    else if(this.state.password==""){
      alert('Please enter the password')
    }
    else if(this.state.email==""){
      alert('Please enter the email')
    }
    else if(this.state.phone==""){
      alert('Please enter the phone')
    }
    else if(this.state.address==""){
      alert('Please enter the address')
    }
    else{


  const url = "http://localhost:9000/person";
    let headers = new Headers();

    headers.append('Content-Type','application/json');
    headers.append('Accept','application/json');

    headers.append('Access-Control-Allow-origin',url);
    headers.append('Access-Control-Allow-Credentials','true');

    headers.append('POST','GET');

    fetch(url, {
       headers:headers,
       method: 'POST',
       body: JSON.stringify(body)
    })
    .then(response => response.json())
    .then(contents => {console.log(contents);

 })
 .catch(()=> console.log("can't access" + url + "response. "))


 alert('Details are submitted successful');

  }
}






  render() {
    return (
      <div>
      <form onSubmit = {this.handleSubmit }>
      <div className="base-container">
        <div className="header">Register</div>
        <div className="content">
          <div className="form">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" name="name" placeholder="name" value={ this.state.name } onChange = { this.handleNameChange } required/>
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="text" name="password" placeholder="password" value={ this.state.password} onChange = { this.handlePasswordChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="text" name="email" placeholder="email" value={ this.state.email } onChange = { this.handleEmailChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="phone">Phone</label>
              <input type="text" name="phone" placeholder="phone" value={ this.state.phone } onChange = { this.handlePhoneChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input type="text" name="address" placeholder="address" value={ this.state.address} onChange = { this.handleAddressChange } required/>
            </div>
          </div>
        </div>
        <div className="footer">
          <button type="submit" onClick = {this.handleSubmit} className="btn">
            Register
          </button>
          <br/><br/><br/>
          <a href="/home"><u>back</u></a>
        </div>
      </div>
      </form>
      </div>

    );
  }
}
