import React from "react";
import donorhome from '../img/donorhome.jpg';
import './d_home.css';
import './Nav.css';
import pit from '../img/pit.jpg';

var eid,profile,img1,img2;


const donor ={
    backgroundImage: 'url(' + donorhome+ ')',
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%',
}

class Donor extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pname:null,
      quant:null,
      quality:null,
      date:null,
      des:null,
      category:null,
      file1:null,
      file2:null,

    }

    this.handleImage1 = this.handleImage1.bind(this)
    this.handleImage2 = this.handleImage2.bind(this)
    this.pnameChange = this.pnameChange.bind(this);
    this.quantChange = this.quantChange.bind(this);
    this.qualityChange = this.qualityChange.bind(this);
    this.dateChange = this.dateChange.bind(this);
    this.desChange = this.desChange.bind(this);
    this.categoryChange = this.categoryChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this)

    eid=this.props.match.params.id;
    console.log(eid);
    profile="/donorprofile/"+eid;


  }
  pnameChange=event=>{
    this.setState({
      pname: event.target.value
    });
  }
  quantChange=event=>{
    this.setState({
      quant: event.target.value
    });
  }
  qualityChange=event=>{
    this.setState({
      quality: event.target.value
    });
  }
  dateChange=event=>{
    this.setState({
      date: event.target.value
    });
  }
  desChange=event=>{
    this.setState({
      des: event.target.value
    });
  }
  categoryChange=event=>{
    this.setState({
      category: event.target.value
    });
  }
  handleImage1(event) {
    this.setState({
      file1: URL.createObjectURL(event.target.files[0])
    });
    img1=event.target.files[0].name;
    console.log(img1) 

  }
  handleImage2(event) {
    this.setState({
      file2: URL.createObjectURL(event.target.files[0]),
    });

  }
  handleSubmit(event) {

    event.preventDefault();
    console.log(this.state)
     var body = {

      email : this.state.email,
      password : this.state.password,
      username : this.state.name,
      address : this.state.address,
      phone:this.state.phone

    }
    console.log(body);


  const url = "http://localhost:9000/person";
    let headers = new Headers();

    headers.append('Content-Type','application/json');
    headers.append('Accept','application/json');

    headers.append('Access-Control-Allow-origin',url);
    headers.append('Access-Control-Allow-Credentials','true');

    headers.append('POST','GET');

    fetch(url, {
       headers:headers,
       method: 'POST',
       body: JSON.stringify(body)
    }).then(response => {if(response.ok){
      alert('Details submitted');
    }
    })
 .catch((error)=> {
   console.error(error) ;
  },
  alert("Email already exists"));

}
  render() {
    return (

      <div style={donor} class="main">
          <div class="home">
            <div class="nav">
            <ul>
                <li><a class="active" href="/"><img alt="sorry" height="120%" width="15%" src={pit}/></a></li>
                <li><a class="links" href="/login">LogOut</a></li>
                <li><a class="links" href={profile}>MyProfile</a></li>
            </ul>
            </div>

        </div>
          <div class="container">
                <form>
                  <div class="row">
                    <div class="col-25">
                      <label for="fname">Product Name</label>
                    </div>
                    <div class="col-75">
                      <input type="text" id="pname" name="pname" placeholder="product name.." value={this.state.pname} onChange={this.pnameChange} required/>
                    </div>
                    <div class="col-25">
                      <label for="quant">Quantity</label>
                    </div>
                    <div class="col-75">
                      <input type="text" id="quant" name="quant" placeholder="quantity.." value={this.state.quant} onChange={this.quantChange} required/>
                    </div>
                  </div>

                  <div class="row">
                    <div class="col-25">
                      <label for="quantity">Quality</label>
                    </div>
                    <div class="col-75">
                      <select id="quality" name="quality" value={this.state.quality} onChange={this.qualityChange} required>
                      <option>select</option>
                        <option value="used">Used</option>
                        <option value="new">New</option>
                      </select>
                    </div>
                    <div class="col-25">
                      <label for="date">Expected date of delievery</label>
                    </div>
                    <div class="col-75">
                      <input type="date" name="date" value={this.state.date} onChange={this.dateChange} required/>
                    </div>
                  </div>
                  <div class="row">
                    <div class="col-25">
                      <label for="des">Description</label>
                    </div>
                    <div class="col-75">
                      <textarea id="des" name="des" placeholder="Write something.." value={this.state.des} onChange={this.desChange} required></textarea>
                    </div>
                    <div class="col-25">
                      <label for="quantity">Category of product</label>
                    </div>
                    <div class="col-75">
                      <select id="category" name="category" value={this.state.category} onChange={this.categoryChange} required>
                      <option>select</option>
                        <option value="cars">Cars</option>
                        <option value="furnitures">Furniture</option>
                        <option value="eanda">Electronics&Appliances</option>
                        <option value="mobile">Mobiles</option>
                        <option value="bikes">Bikes</option>
                        <option value="stationery">Stationery</option>
                        <option value="fashion">Fashion</option>
                      </select>
                    </div>
                    </div>
                    <div class="row">
                    <div class="col-25">
                      <label for="images">Product Images</label>
                    </div>
                    <br/>
                    <div class="fileimages">
                      <input type="file" onInput={this.handleImage1} required/>
                      <div class="imgfile">
                      <img class="upload" src={this.state.file1}/>
                      </div>
                    </div>
                    <div class="fileimages">
                      <input type="file" onInput={this.handleImage2} required/>
                      <div class="imgfile">
                      <img class="upload" src={this.state.file2}/>
                      </div>
                    </div>
                  <br/>
                  </div> <br/> <br/>
                  <div class="row"><center>
                    <button type="submit" class="submit" onClick={this.handleSubmit.bind(this)} value="Submit">Submit</button></center>
                  </div>
                </form>

        </div>
      </div>
    );
  }
}
export default Donor
