    this.onCellChange=this.onCellChange.bind(this)

       }

   onCellChange(event){

    console.log("in cell change:"+event.target.value)
    } 

    renderResultRows(){

        let s=this.state.s
        let n
        return s.map((song,index) => {
           let i='/productimages/'+song.img1
           console.log(i,typeof(i))
            return (
                <tr >

                    <td >{song.pname}</td>
                    <td >{song.did}</td>
                    <td >{song.quant}</td>
                    <td >{song.quality}</td>
                    <td><img src={window.location.origin + i}/></td>
                    <td id="nprice" contenteditable='true' ></td>



                    <td><button onClick={() => this.fetchDetails(index)} /></td>
                </tr>
            );
        });  


    }

    fetchDetails(row){
        let s=this.state.s
        console.log(s[row])
        //window.location.href="popupform/:"+s[row]


    }

   products(){
        const url = "http://localhost:9000/products";

        let headers = new Headers();
        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

         fetch(url,{
            headers:headers,
            method: 'GET',
            })
            .then(response=>{                
                return response.json()
            }).then(res=>{this.setState({s:res})
            console.log(this.state.s) })



        }
        componentDidMount(){this.products()}

        render() {
            let i='/productimages/bajaj1.jpeg'

            return (

                <div style={admin} class="adminmain">
                    <AdminNav/>
                    <table id="product" class="atable" border="1" onCellChange={this.onCellChange}>
                      {this.renderResultRows()} 
                    </table>
                    <img src={window.location.origin + i}/>
                </div>
            )
        }
}

export default Validate
