import React from "react";
import DonorNav from './Nav';
import './myprofile.css';
import donorhome from '../img/donorhome.jpg';
const donor ={
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%',
}
var eid;

class DonorProfile extends React.Component {
    constructor(props) {
      super(props);
      this.state={

        email : '',
        name : '',
        address : '',
        phone:''

      }
      this.handleNameChange=this.handleNameChange.bind(this)
      this.handleEmailChange=this.handleEmailChange.bind(this)
      this.handleAddressChange=this.handleAddressChange.bind(this)
      this.handlePhoneChange=this.handlePhoneChange.bind(this)

      eid=this.props.match.params.id;
      console.log(eid);
       const url = "http://localhost:9000/login1";

          let headers = new Headers();

           headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

            headers.append('Access-Control-Allow-origin',url);
            headers.append('Access-Control-Allow-Credentials','true');

            headers.append('POST','GET');
          var body={id:eid};
           var s = fetch(url, {
              headers:headers,
              method: 'POST',
              body: JSON.stringify(body)
            }).then(response => 
              response.text().then(data => ({
                  data1: (data)
              })
          ).then(res => {
              console.log(res.data1)
              var obj=JSON.parse(res.data1)
              //console.log(obj.name)
             this.setState({
               name:obj.name,
               email:obj.email,
               phone:obj.phone,
               address:obj.address,
              });


             // console.log(this.state.name)

        }));
        console.log(this.state.name)

    }

      handleNameChange=event=>{
        this.setState({
          name : event.target.value
        });
      }

      handleEmailChange=event=>{
        this.setState({
          email: event.target.value
        });
      }

      handlePhoneChange=event=>{
        this.setState({
          phone: event.target.value
        });
      }

      handleAddressChange=event=>{
        this.setState({
          address: event.target.value
        });
      }

    // componentDidMount() {
    //     const { match: { params } } = this.props;

    //     fetch.get(`/http://localhost:9000/login1/${params.id}`)
    //       .then(({ data: user }) => {
    //         console.log('user', user);

    //         //this.setState({ user });
    //       });
    //   }


    render(){
        return(
            <div style={donor} class="profile">
          <DonorNav/>
          <div class="pcontainer">
                <form>
                  <h3>Personal Details</h3><center>
                    <div class="row">
                        <div class="col-25">
                            <h5>Name</h5>
                        </div>
                        <div class="col-75">
                          <h5>{this.state.name}</h5>
                        </div>
                        </div>
                      <div class="row">
                        <div class="col-25">
                            <h5>Email</h5>
                        </div>
                        <div class="col-75">
                            <h5>{this.state.email}</h5>
                        </div>

                    </div>
                    <div class="row">
                        <div class="col-25">
                          <h5>Phone Number</h5>
                        </div>
                        <div class="col-75">
                            <h5>{this.state.phone}</h5>
                        </div>
                      </div>
                      <div class="row">
                        <div class="col-25">
                            <h5>Address</h5>
                        </div>
                        <div class="col-75">
                            <h5>{this.state.address}</h5>
                        </div>
                    </div>
                    </center>
                </form>
            </div>

          </div>

        )
    }
}

export default DonorProfile
