}
var eid;

class Cproducts extends React.Component {
    constructor(props) {
      super(props);
      this.state={


        d:[],
        dd:false
      }

      eid=sessionStorage.getItem("id");
      console.log(eid);

    }

    donationdetails(){
      const url = "http://localhost:9000/cprofile";

      let headers = new Headers();
           headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

            headers.append('Access-Control-Allow-origin',url);
            headers.append('Access-Control-Allow-Credentials','true');

            headers.append('POST','GET');
            var body={cid:sessionStorage.getItem("id")};
           var s = fetch(url, {
              headers:headers,
              method: 'POST',
              body: JSON.stringify(body)
            }).then(response=>{return response.json()}).then((res)=>{
              this.state.d=res
              this.state.dd=true
            console.log(this.state.d)})


    }
    renderResultRows(){

      let s=this.state.d
      let n
      return s.map((song,index) => {
         let i='/productimages/'+song[1]
         //console.log(i,typeof(i))
         let a="nprice-"+index
          return (
              <tr id={index} class="foralign">
                  <td >{song[0]}</td>
                  <td><img src={i} height="200px" width="150px"/></td>
                  <td>{song[2]}
                  </td>
                  <td>{song[3]}
                  </td>


              </tr>
          );
      });  


  }



    componentDidMount(){

      this.donationdetails();
    }

    render(){
        return(
            <div style={donor} class="profile">
          <DonorNav/>

           <div className="customertable">

      <table class="w3-table-all" id="table">
                        <th>Name</th>
                        <th>Product Image</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Product status</th>  
      <tbody>{this.renderResultRows()}</tbody>

    </table>
            </div>
            </div>




        )
    }
}

export default Cproducts
