import React from "react";
import { Redirect,useHistory } from "react-router-dom";

export class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      email : '',
      password : ''
    }
    this.handleChange = this.handleChange.bind(this)
    this.handledonateSubmit = this.handledonateSubmit.bind(this)
    this.handlepurchaseSubmit = this.handlepurchaseSubmit.bind(this)
  }
  handleChange(event){
    const { name, value } = event.target
    this.setState({
      [name] : value
    })
  }

  handledonateSubmit(event){
    this.props.hitory.push("/donorhome");
  }
  handlepurchaseSubmit(event){
    event.preventDefault()
  }
  /*donate = () =>{
    let path = '';
    let history = useHistory();
    history.push(path);
  }*/

  render() {
    return (
      <form>
      <div className="base-container" ref={this.props.containerRef}>
        <div className="header">Login</div>
        <div className="content">
          <div className="form">
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="text" name="email" placeholder="email" value={ this.state.email } onChange = { this.handleChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="password" name="password" placeholder="password" value={ this.state.password} onChange = { this.handleChange } required/>
            </div>
          </div>
        </div>
        <div className="footer">
          <button type="submit" className="btn" onClick={this.handledonateSubmit.bind(this)}> 
            Donate  
          </button><br/><br/>
          <button type="submit" className="btn" onClick={this.handlepurchaseSubmit.bind(this)}>
            Purchase
          </button><br/><br/><br/>
          <a href="/home"><u>back</u></a>
        </div>
      </div>
      </form>
    );
  }
}
