// }


//  const images = importAll(require.context('../productimages', true,/\.(png|jpeg|svg)$/))
//const imagePath = (name) => images(name, true)

const admin = {
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%', 
}

const table = {
    top:'20%',
    left:'40%'
}

class Validate extends React.Component {

    constructor(props) {
        super(props);
         this.state={
           s:[],
           p:null,
           showPopup: false
         }


    //this.updatenominal=this.updatenominal.bind(this)
    this.onCellChange=this.onCellChange.bind(this)

       }
       togglePopup() {
        this.setState({
          showPopup: !this.state.showPopup
        });
      }
   onCellChange(event){

    console.log("in cell change:"+event.target.value)
    } 


    renderResultRows(){

        let s=this.state.s
        let n
        return s.map((song,index) => {

            return (

                <tr >

                    <td >{song.pname}</td>
                    <td >{song.did}</td>
                    <td >{song.quant}</td>
                    <td >{song.quality}</td>
                    <td >{song.img1}</td>
                    <td id="nprice" contenteditable='true' ></td>



                    <td><button onClick={() => this.fetchDetails(index)} /></td>
                </tr>

            );
        });  


    }

    fetchDetails(row){
      return(
        <div className='app'>
      <h1>hihi</h1>
      <button onClick={this.togglePopup.bind(this)}>show popup</button>


      {this.state.showPopup ? 
        <Popup
          text='Close Me'
          closePopup={this.togglePopup.bind(this)}
        />
        : null
      }
    </div>

    )

    }

   products(){
        const url = "http://localhost:9000/products";

        let headers = new Headers();
        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

         fetch(url,{
            headers:headers,
            method: 'GET',
            })
            .then(response=>{                
                return response.json()
            }).then(res=>{this.setState({s:res})
            console.log(this.state.s) })



        }
        componentDidMount(){this.products()}
        render() {
            return (
              <div className='popup'>
                <div className='popup_inner'>
                  <h1>{this.props.text}</h1>
                <button onClick={this.props.closePopup}>close me</button>
                </div>
              </div>
            );
          }


        render() {


            return (

                <div style={admin} class="adminmain">
                    <AdminNav/>
                    <table id="product" class="atable" border="1" onCellChange={this.onCellChange}>
                      {this.renderResultRows()} 
                    </table>
                    <div id="content"></div>
                </div>

            )
        }
}

export default Validate
