import React from "react";
import DonorNav from './Nav';
import './myprofile.css';
import UserProfile from './UserProfile'

const donor ={
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%',
}
var eid;

class DonorProfile extends React.Component {
    constructor(props) {
      super(props);
      this.state={

        email : '',
        name : '',
        address : '',
        phone:''
      }
      this.handleNameChange=this.handleNameChange.bind(this)
      this.handleEmailChange=this.handleEmailChange.bind(this)
      this.handleAddressChange=this.handleAddressChange.bind(this)
      this.handlePhoneChange=this.handlePhoneChange.bind(this)

      eid=this.props.match.params.id;
      console.log(eid);

       const url = "http://localhost:9000/login1";

          let headers = new Headers();
           headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

            headers.append('Access-Control-Allow-origin',url);
            headers.append('Access-Control-Allow-Credentials','true');

            headers.append('POST','GET');
            var body={id:eid};
           var s = fetch(url, {
              headers:headers,
              method: 'POST',
              body: JSON.stringify(body)
            }).then(response => 
              response.text().then(data => ({
                  data1: (data)
              })
          ).then(res => {
              console.log(res.data1)
              var obj=JSON.parse(res.data1)
              //console.log(obj.name)
             this.setState({
               name:obj.name,
               email:obj.email,
               phone:obj.phone,
               address:obj.address,
              });


             // console.log(this.state.name)

        }));


    }

      handleNameChange=event=>{
        this.setState({
          name : event.target.value
        });
      }

      handleEmailChange=event=>{
        this.setState({
          email: event.target.value
        });
      }

      handlePhoneChange=event=>{
        this.setState({
          phone: event.target.value
        });
      }

      handleAddressChange=event=>{
        this.setState({
          address: event.target.value
        });
      }

      handleSubmit(event) {

        event.preventDefault();
        //console.log(this.state.name,this.state.email,this.state.address,this.state.phone)

         var body = {

          email : this.state.email,
          username : this.state.name,
          address : this.state.address,
          phone:this.state.phone

        }
        console.log(body);
        if(this.state.name==""){
          alert('Please enter the name')

        }

        else if(this.state.email==""){
          alert('Please enter the email')
        }
        else if(this.state.phone==""){
          alert('Please enter the phone')
        }
        else if(this.state.address==""){
          alert('Please enter the address')
        }
        else{


      const url = "http://localhost:9000/updateperson";
        let headers = new Headers();

        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

        fetch(url, {
           headers:headers,
           method: 'POST',
           body: JSON.stringify(body)
        }).then(response => {if(response.ok){
          alert('Details submitted');
          window.location.reload(false);
        }
        })

      }
    }





    render(){
        return(
            <div style={donor} class="profile">
          <DonorNav/>
          <div class="pcontainer">
          <form>
      <div className="base-container">
        <div className="header">Register</div>
        <div className="content">
          <div className="form">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" class="form-control" name="name" placeholder="name" value={ this.state.name } onChange = { this.handleNameChange } required/>
            </div>


            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="text" name="email" placeholder="email" value={ this.state.email } onChange = { this.handleEmailChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="phone">Phone</label>
              <input type="number" name="phone" placeholder="phone" value={ this.state.phone } onChange = { this.handlePhoneChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input type="text" name="address" placeholder="address" value={ this.state.address} onChange = { this.handleAddressChange } required/>
            </div>
          </div>
        </div>

          <button type="submit" onClick = {this.handleSubmit} className="btn">
            Update
          </button>
          <br/>


      </div>
      </form>
            </div>

          </div>

        )
    }
}

export default DonorProfile
