import React from "react";
import CNav from './Nav';
import './c_home.css'
import { BrowserRouter, Route } from 'react-router-dom';
import Sidenav from "./sidenav"
var id,s1,s2;
const donor ={
  width: '100%',
  height: '100%',
  backgroundColor: '#dcdcdc',
}
class productalone extends React.Component {
    constructor(props) {
      super(props);
        id=this.props.match.params.id
      console.log(id)
      this.state={
        cp:[],

      }
    }

    componentDidMount(){
      const url = "http://localhost:9000/singleproduct";
       var body={id:id}
        let headers = new Headers();
        headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

        headers.append('Access-Control-Allow-origin',url);
        headers.append('Access-Control-Allow-Credentials','true');

        headers.append('POST','GET');

         fetch(url,{
            headers:headers,
            method: 'POST',
            body: JSON.stringify(body)
            })
            .then(response =>{return response.json()})
            .then(res=>{this.setState({cp:res})
            console.log(this.state.cp)

            console.log(s1,s2)

        })
    }


    render() {
      s1='/productimages/'+this.state.cp.img1
      s2='/productimages/'+this.state.cp.img2
      return (
        <div style={donor} class="main">
                <CNav/>
                <Sidenav/>
                <div class="productalone">
                    <div class="divleft">
                        {this.state.cp.pname}
                        <img src={s1} width="400px"/>
                        <img src={s2} width="400px"/>
                    </div>
                </div>
        </div>

      );
    }
  }

  export default productalone;
