import React from "react";
import DonorNav from './Nav';
import './d_home.css';
import donorhome from '../img/donorhome.jpg';
const donor ={
    backgroundImage: 'url(' + donorhome + ')',
    width: '100%',
    height: '100%',
    backgroundRepeat: 'no-repeat',
    backgroundSize: '100% 100%',
}

class DonorProfile extends React.Component {
    constructor(props) {
      super(props);
    //   this.state={
    //       uemail:null
    //   }
     var eid=this.props.match.params.id;
      console.log(eid);
       const url = "http://localhost:9000/login1";

          let headers = new Headers();

           headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

            headers.append('Access-Control-Allow-origin',url);
            headers.append('Access-Control-Allow-Credentials','true');

            headers.append('POST','GET');
          var body={id:eid};
           var s = fetch(url, {
              headers:headers,
              method: 'POST',
              body: JSON.stringify(body)
            }).then(response => 
              response.text().then(data => ({
                  data1: (data)
              })
          ).then(res => {
              console.log(res.data1)

        }));


    }

    // componentDidMount() {
    //     const { match: { params } } = this.props;

    //     fetch.get(`/http://localhost:9000/login1/${params.id}`)
    //       .then(({ data: user }) => {
    //         console.log('user', user);

    //         //this.setState({ user });
    //       });
    //   }


    render(){
        return(
            <div style={donor} class="main">
          <DonorNav/>
          <div className="base-container">
        <div className="header">Register</div>
        <div className="content">
          <div className="form">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" name="name" placeholder="name" value={ this.state.name } onChange = { this.handleNameChange } required/>
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="text" name="password" placeholder="password" value={ this.state.password} onChange = { this.handlePasswordChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="text" name="email" placeholder="email" value={ this.state.email } onChange = { this.handleEmailChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="phone">Phone</label>
              <input type="number" name="phone" placeholder="phone" value={ this.state.phone } onChange = { this.handlePhoneChange } required/>
            </div>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input type="text" name="address" placeholder="address" value={ this.state.address} onChange = { this.handleAddressChange } required/>
            </div>
          </div>
        </div>
        </div>
          </div>

        )
    }
}

export default DonorProfile
