import React from "react";
import CNav from './Nav';
var eid;
class Cart extends React.Component {
    constructor(props){
        super(props);
        this.state={
            d:[]
        }
    }
    componentDidMount(){
        const url = "http://localhost:9000/cartprofile";
      console.log(sessionStorage.getItem("id"))
      let headers = new Headers();
           headers.append('Content-Type','application/json');
        headers.append('Accept','application/json');

            headers.append('Access-Control-Allow-origin',url);
            headers.append('Access-Control-Allow-Credentials','true');

            headers.append('POST','GET');
            var body={cid:sessionStorage.getItem("id")};
           var s = fetch(url, {
              headers:headers,
              method: 'POST',
              body: JSON.stringify(body)
            }).then(response=>{return response.json()}).then((res)=>{
              this.state.d=res

            console.log(this.state.d)})
    }
    renderResultRows(){

        let s=this.state.d
        let n
        return s.map((song,index) => {
           let i='/productimages/'+song[4]
           //console.log(i,typeof(i))
           let a="nprice-"+index
            return (
                <tr id={index}>

                    <td >{song[1]}</td>
                    <td >{song[3]}</td>
                    <td><img src={i} class="validateimage"/></td>
                    <td contentEditable="true" id={a} width="100px"></td>
                    <td><button onClick={() => this.fetchDetails(index)} > Validate </button></td>
                    <td><button onClick={() => this.deleteDetails(index)} > Delete </button></td>
                     </tr>
            );
        });  


    }
    render()
    {
        return(
            <div>
            <CNav/>
            <table id="product" class="atab" border="1">
                        <th>Name</th>
                        <th>Image</th>
                        <th>Agreed Price</th>
                        <th>Buy</th>

                     <tbody> {this.renderResultRows()} </tbody>
            </table>
            </div>
        )
    }
}
export default Cart;
